/* CSS Overrides go here */
/*------
header
--------*/
.header__content--desktop {
padding: 2% 0;
}
/*-----
cta-btn
-----*/
.cta-btn {
  border: 1px solid #ffffff;
  background-color: #FFE168;
  padding: .7rem 4rem;
  border-radius: 8px;
  margin: 0 auto;
  display: block;
}
.cta-btn-p {
  text-align: center;
  font-size: 10px;
  margin-top: .5rem;
  letter-spacing: 1px;
  line-height: .7rem;
  font-weight:bold;
}
/*------
drawer-menu
--------*/
.drawer-container {
  top:65px;
  height:calc(100vh - 65px);
}
@media screen and (max-width:768px){
.drawer-container {
  top:58px;
  height:calc(100vh - 58px);
  }
}
/*------
General
-------*/
.section-title-container{
   display: flex;
   align-items: center;
   justify-content: center;
}
.user-section-title {
  font-weight: bold;
  margin: 0;
}
.span1, .span2, .span3 {
   display: block;
   background-color: #ffc107;
   border-radius: 50%;
   margin: 0 1rem;
}
.span1 {
   width: 15px;
   height: 15px;
}
.span2 {
   width: 10px;
   height: 10px;
}
.span3 {
   width: 5px;
   height: 5px;
}
@media screen and (max-width:768px){
  .span1, .span2, .span3 {
  margin:0 .5rem;
  }
}
.user-widestyle-p {
  text-align:left;
  margin:1rem 0;
  letter-spacing:2px;
  line-height:2.0;
  color:#2c3e50bf;
  font-size:14px;
}
@media screen and (max-width:768px){
  .user-widestyle-p {
    padding:0;
  }
}
/*-------
お知らせ
--------*/
#section-1661383558235 {
  height: 300px;
  overflow-y: scroll;
  margin:0 auto;
}
.information-text {
  margin:2px 0;
  border-bottom:1px solid #e1e1e1;
  padding-bottom: .5rem;
}
.information-tag::before {
content:"";
font-size:10px;
margin-right:16px;
border-radius:5px;
color:white;
padding:2px 2px;
display:inline-block;
width:100px;
text-align:center;
}
.event-seminar-tag::before{
content:"イベント・セミナー";
background-color:#7374C7;
}
.club-tag::before{
content:"クラブ活動";
background-color:#C7514C;
}
.news-tag::before{
content:"ニュース";
background-color:#5FC785;
}
.teach-me-tag::before{
content:"教えて○○先生";
background-color:#4C4CC7;
}
.new-contents-tag::before{
content:"新着コンテンツ";
background-color:#C76D30;
}
/*-------
学べるジャンル
--------*/
.categories-card {
      display:flex;
      border:1px solid #d1d1d1;
      border-radius:5px;
      padding: 1rem;
      position:relative;
      background-color: white;
      cursor: pointer;
  align-items:center;
    }
    .categories-card-img{
      width:56px;
      height:56px;
    }
    .categories-card-text {
      margin-left: 1rem;
    }
     .categories-name {
       margin:0;
       padding:0;
      }
     .caegories-p {
       margin:0;
       padding:8px 4px;
      font-size:11px;
      color:gray;
     }
    .categories-card::after {
      content:"";
      border-top:1px solid #17ab65;
      border-right:1px solid #17ab65;
      height:12px;
      width:12px;
      position:absolute;
      transform: rotate(45deg);
      right:10px;
    }
#block-1672486276804,#block-1672486293981{
    padding:0 4px 8px 15px!important;
  }
#block-1672486246036, #block-1672486254102 {
    padding:0 15px 8px 4px!important;
  }
#block-1672486138502,#block-1672486282032,#block-1672486001050,#block-1672486287580{
  padding:0 4px 8px 4px!important;

}
@media screen and (max-width:768px){
  #block-1672486001050, #block-1672486138502, #block-1672486246036, #block-1672486254102, #block-1672486276804, #block-1672486282032, #block-1672486287580, #block-1672486293981{
    width: 50%;
  }
#block-1672486001050,#block-1672486276804,#block-1672486287580, #block-1672486293981{
    padding:0 2.5px 5px 10px!important;
  }
#block-1672486138502, #block-1672486246036, #block-1672486254102, #block-1672486282032 {
    padding:0 10px 5px 2.5px!important;
  }

  .categories-card {
    padding:.7rem .3rem;
  }
  .categories-card::after {
      display:none;
    }
}

/*-------
